/**
 *  Renders the sitemap as plain text.
 *
 *  @class TextRenderer
 */
class TextRenderer {

  /**
   *  Render the plain text format.
   *
   *  @static {function} render
   *  @param {Object} context the processing context.
   *  @param {Object} sitemap raw sitemap data.
   *
   *  @returns an object with the text file `content`.
   */
  static render (context, sitemap) {
    const pages = sitemap.files.map((info) => {
      return info.url
    })
    // NOTE: many line parsers expect a trailing newline to operate
    // NOTE: correctly so we add it to be on the safe side
    return {content: pages.join('\n') + '\n'}
  }

  /**
   *  Get the file extension for the `text` format.
   *
   *  @static {property} extension
   */
  static get extension () {
    return 'txt'
  }
}

module.exports = TextRenderer
